import './TaskList.css';
import { useState,useEffect } from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';

function TaskList(){

    const navigate = useNavigate();
    const[getList,setList] = useState([]);


    useEffect(()=>{
     axios.get('http://localhost:8080/api/v1/tasks').then((result)=>{
         setList(result.data.data);
     }).catch((error)=>{
        console.log(error);
     })
    },[])


    const onCreateTask=()=>{
        navigate('/create')
    }

    const onSingleTask=()=>{

    }






    return (<div className="container">
    <h1>Task List</h1>
    <button type="submit" onClick={onCreateTask} className="btn btn-success btn-md from-button">New Task</button>
     {
        getList.map((data,index)=>{
           return(<div key={index}>
              <h2>{data.title}</h2>
              <p></p>
              <h4>{data.description}</h4>
              <button type="button" onClick={onSingleTask} className="btn btn-success btn-md from-button">View Details</button>
            </div>
            )
        })
     }
</div>)
}
export default TaskList;
