import './TaskList.css';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function TaskList() {

    const navigate = useNavigate();
    const [getList, setList] = useState([]);


    useEffect(() => {
        axios.get('http://localhost:8080/api/v1/tasks').then((result) => {
            setList(result.data.data);
        }).catch((error) => {
            console.log(error);
        })
    }, [])


    const onCreateTask = () => {
        navigate('/create')
    }

    const onSingleTask = () => {
     navigate(``)
    }

    const dateFormat = (input) => {
        let result = "";
        let date = new Date(input);
        let day = date.getDate();
        let month = date.getMonth() + 1;
        let year = date.getFullYear();
        result += day.toString().length <= 1 ? `0${day}/` : `${day}/`;
        result += month.toString().length <= 1 ? `0${month}/` : `${month}/`;
        result += year;
        return result;
    }


    return (<div className="container">
        <h1>Task List</h1>
        <button type="submit" onClick={onCreateTask} className="btn btn-success btn-md from-button">New Task</button>
        {
            getList.map((data, index) => {
                return (<div className="list" key={index}>
                    <h2 className="details">{data.title}</h2>
                    <p className="details">{dateFormat(data.createdAt)}</p>
                    <h4 className="details">{data.description}</h4>
                    <button type="button" onClick={onSingleTask} className="btn btn-primary btn-md from-button">View Details</button>
                </div>
                )
            })
        }
    </div>)
}
export default TaskList;
