import './TaskDetails.css';
import { useState, useEffect } from 'react';
import { useNavigate,useParams } from 'react-router-dom';
import axios from 'axios';
import { dateFormat } from '../../helpers/dateFormat';

function TaskDetails() {

    const navigate = useNavigate();
    const [getSingleList, setSingleList] = useState({});


    useEffect(() => {
        const { id } = useParams();
        console.log(id);
        axios.get(`http://localhost:8080/api/v1/tasks/${id}`).then((result) => {
            setSingleList(result.data.data);
            console.log(result.data.data);
        }).catch((error) => {
            console.log(error);
        })
    }, [])


    const allTask = () => {
        navigate('/')
    }

    return (<div className="container">
        <h1>Task Title:</h1>

        <button type="submit" onClick={allTask} className="btn btn-success btn-md from-button">All Task</button>
    </div>)
}
export default TaskDetails;
